mapper.select:
select scorecard.*, topic_assign.student_id, topic_assign.topic_id, topic_select_table.name as topic_name, topic_select_table.description as topic_desc, teacher.name as teacher_name, student.name as student_name, graduate_info.id as graduate_info_id, graduate_info.title as graduate_title
from scorecard join topic_assign on scorecard.assign_id = topic_assign.id join topic_select_table on topic_assign.topic_id = topic_select_table.id join teacher on topic_select_table.teacher_id = teacher.id join student on topic_assign.student_id = student.id join graduate_info on graduate_info.id = topic_select_table.graduate_info_id
where graduate_info.id = #{graduateInfoId} and scorecard.id = #{cardId} and teacher.id = #{teacherId} and student.id = #{studentId}
